import React, {useEffect, useRef, useState} from 'react';




/*
    PROPS


    1 type
      - text
      - photoRight
      - photoLeft

    2 text
      - your text

    3 image
      - your image

    4 cloud
      - true
      - false
    5 img
      - your image

 */
function BlogDiv(props) {
    useEffect(() => {
        setTextImageHeight(refBody.current.scrollHeight)
        console.log(refBody.current.scrollHeight)
    }, [])
    const refBody = useRef(null)
    const [textImageHeight, setTextImageHeight] = useState('0')

    const css = {
        body : {
            width: props.cloud ? '70%' : 'calc(100% - 60px)',
            height: 'auto',
            marginRight: props.cloud ? '15%' : '0',
            marginLeft: props.cloud ? '15%' : '0',
            marginTop: props.cloud ? '50px' : '0',
            marginBottom: props.cloud ? '50px' : '0',

            padding: '30px',

            backgroundColor: '#0d1a2c',

            borderRadius: props.cloud ? '15px' : '0',

            fontSize: '16px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',

        },
        text : {
            width: props.cloud ? '80%' : '60%',
            wordWrap: 'break-word',
        },
        image : {
            maxWidth: props.cloud ? '20%' : '35%',
        },
        textImage : {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: 'white',
            width: props.cloud ? '80%' : '60%',
            height: textImageHeight,
        },
    }




    if (props.type === 'text') {
        return (
            <div style={css.body}>
                <text style={css.text}>{props.text}</text>
            </div>
        )
    } else if (props.type === 'photoRight') {
        return (
            <div style={css.body} ref={refBody}>
                <div style={css.textImage}>
                    <text style={css.text}>{props.text}</text>
                </div>
                <img src={props.img} alt="фоточка" style={css.image}/>
            </div>
        )
    } else if (props.type === 'photoLeft') {
        return (
            <div style={css.body}>
                <img src={props.img} alt="фоточка" style={css.image}/>
                <div style={css.textImage}>
                    <text style={css.text}>{props.text}</text>
                </div>
            </div>
        )
    } else {
        console.log('ERROR')
    }
}

export default BlogDiv
