function BlankTextImage(props) {
    let css = {
        body : {
            width: '100$',
            backgroundColor: '#070e17',
            display: 'flex',
            justifyContent: 'space-around',
            color: 'white',
        },
        divText : {
            width: '600px',
            display: 'flex',
            justifyContent: 'flexStart',
            flexDirection: 'column',
            textAlign: 'left',



        },
        topText : {

            textTransform: 'uppercase',
            color: '#3A68B8',
            fontSize: '18px',
            lineHeight: '160%',
        },
        mainText : {
            color: '#FFFFFF',
            opacity: '0.7',
        },
        divButtons : {
            width: '500px',
            display: 'flex',
            justifyContent: 'space-around'
        },
        button : {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '10px',
            paddingRight: '20px',
            paddingLeft: '20px',
            fontSize: '20px',
            lineHeight: '27px',
            color: 'white',
            backgroundColor: '#3A68B8',
            border: 'none',
            borderRadius: '5px',
        },
        img : {
            height: '634px'
        },
        playIcon : {
            height: '26px'
        }


    }


    return (
        <div style={css.body}>
            <div style={css.divText}>
                <h3 style={css.topText}>Next genaretion platform</h3>
                <h1>Artificial intelligence<br /> & Syber security</h1>
                <text style={css.mainText}>Lorem ipsum is placeholder text commonly used in the graphic,<br />
                    print, and publishing industries for previewing layouts and<br />
                    visual mockups.</text>
                <div style={css.divButtons}>
                    <button style={css.button}>Get Started</button>
                    <button style={css.button}><img src={props.playIcon} alt="треугольник игры" style={css.playIcon}/>
                        Watch Video</button>
                </div>

            </div>
            <div>
                <img src={props.img} alt="ракета" style={css.img}/>

            </div>
        </div>
    )
}
export default BlankTextImage;
