function UlFlexImgText(props) {
    const css = {
        body : {
            width: '100%',
            height: 'auto',
            paddingTop: '10px',
            paddingBottom: '10px',
            backgroundColor: props.backgroundColor,
        },
        ul : {
            width: '100%',
            listStyleType: 'none',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '0'
        },
        element : {
            maxWidth: '350px',
            color: 'white',
            border: '10px solid ' + props.backgroundColor,
            backgroundColor: '#122542',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            textAlign: 'center',
            opacity: '0.7',
            padding: '3px'
        },
        elementImg : {
            maxHeight: '100px'
        }
    }
    const UlForm = (count) => {
        let h1Text = eval('props.h1Text' + count);
        let text = eval('props.text' + count);
        return (
            <div style={css.element}>
                <img src={props.img[count - 1]} alt=""
                style={css.elementImg}/>
                <h2>{h1Text}</h2>
                <text>{text}</text>
            </div>
        )
    }
    const countCheck = (count) => {
        if (count === 4) {
            return (
                <ul style={css.ul}>
                    <li>{UlForm(1)}</li>
                    <li>{UlForm(2)}</li>
                    <li>{UlForm(3)}</li>
                    <li>{UlForm(4)}</li>
                </ul>
            )
        } else if (count === 5) {
            return (
                <ul style={css.ul}>
                    <li style={css.li}>{UlForm(1)}</li>
                    <li style={css.li}>{UlForm(2)}</li>
                    <li style={css.li}>{UlForm(3)}</li>
                    <li style={css.li}>{UlForm(4)}</li>
                    <li style={css.li}>{UlForm(5)}</li>
                </ul>
            )
        } else if (count === 6) {
            return (
                <ul style={css.ul}>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                    <li></li>
                </ul>
            )
        }
    }


    return (
        <div style={css.body}>
            {countCheck(props.count)}


        </div>
    )
}

export default UlFlexImgText;
