            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',

        },
        window : {
            width: '400px',
            height: '400px',
            backgroundColor: '#0d1a2c',
            color: 'white',
            paddingLeft: '50px',
            paddingRight: '50px'
        },
        input : {
            width: '80%',
            height: '30px',
            fontSize: '24pxfg'
        },
        checkbox : {
            width: '20px',
            height: '20px',
            marginRight: '10px'
        },
        divFlex : {
            display: 'flex',
            alignItems: 'center',
            marginTop: '10px'
        },
        errorText : {
            color: 'red',
            marginTop: '5px'
        },
        button : {
            backgroundColor: '#142d4f',
            color: 'white',
            border: '0',
            width: '100px',
            height: '40px',
        }

    }
    const checkOnError = (int) => {
        const ERROR = () => {
            return (
                <text style={css.error}> Неправильно введено</text>
            )
        }
        // int = 0 EMAIL / int = 1 PASSWORD
        if (int === 0) {

        } else if (int === 1) {

        } else {
            console.log('ERROR IN SIGN IN')
        }
    }
    return (
        <div style={css.body}>
            <div style={css.window}>
                <br />
                <h1>Sign In</h1>

                <h3>E-mail</h3>
                <input type="text" style={css.input}/><br />
                <text>{checkOnError(0)}</text>
                <h3>Password</h3>
                <input type={visible} style={css.input}/><br />
                <text>{checkOnError(1)}</text>
                <div style={css.divFlex}>
                    <input type="checkbox" style={css.checkbox}
                    onClick={changeVisible}/>
                    <text>Показать пароль</text>
                </div>
                <br />
                <button style={css.button}>Подтвердить</button>
            </div>

        </div>
    )
}
export default SingIn
