import image from "../image"
import {useState} from 'react'

function SingIn() {

    const css = {
        body : {
            width: '400px',
            height: '550px',
            backgroundColor: '#0d1a2c',
            margin: 'auto',
            marginTop: '50px',
            marginBottom: '100px',
            paddingLeft: '50px',
            paddingRight: '50px',
            display: 'flex',
            flexDirection: 'column',
            color: 'white',
            fontSize: '20px',
            fontFamily: 'Arial'
        },
        h1 : {
            marginTop: '30px',
        },
        text : {
            marginTop: '30px',
        },
        input : {
            width: '300px',
            border: '0',
            padding: '0',
            height: '30px',
            marginTop: '5px',
            fontSize: '20px',
            borderRadius: '5px',
        },
        inputClick : {
            background: 'red',
            color: 'white'
        }
    }
    const clickOnInput = (number) => {
        if (number === 1) {
            setcssInput1(css.inputClick)
        } else if (number === 2) {
            setcssInput2(css.inputClick)
        }
    }
    const [cssInput1, setcssInput1] = useState(css.input)
    const [cssInput2, setcssInput2] = useState(css.input)
    return (
        <div style={css.body}>
            <h1 style={css.h1}>Sign In</h1>
            <text style={css.text}>E-mail:</text>
            <input type="text" style={cssInput1}
                   onClick={clickOnInput(1)}/>
            <text style={css.text}>Password:</text>
            <input type="text" style={cssInput2}
                   onClick={clickOnInput(2)}/>
        </div>
    )
}
export default SingIn
