import image from "../image"
import {useState, useRef, useEffect} from 'react'

function SingIn(props) {
    const [register, setRegister] = useState(props.register)
    const [divInput1, setDivInput1] = useState({
        text: '',
        width: 0
    })
    const [divInput2, setDivInput2] = useState({
        text: '',
        width: 0
    })

    const [buttonCon, setButtonCon] = useState(false)
    const buttonConMeneger = useRef(true)
    const input1 = useRef()
    const inputManager1 = useRef(0)



    const focusOnInput = (number) => {
        if (number === 1) {
            setCssInput1(css.inputClick)
        } else if (number === 2) {
            setCssInput2(css.inputClick)
        }
    }
    const unFocusOnInput = (number) => {
        if (number === 1) {
            setCssInput1(css.input)
        } else if (number === 2) {
            setCssInput2(css.input)
        }
    }
    const confirmButton = () => {
        if (buttonCon && buttonConMeneger.current) {
            buttonConMeneger.current = false
            setTimeout(() => {
                buttonConMeneger.current = true
                setButtonCon(false)
            },1500)
            return <img src={image.loading} style={css.image}/>
        } else if (buttonCon) {
            return <img src={image.loading} style={css.image}/>
        }
    }
    const changeWidthInput = (e, number) => {
        setDivInput1({...divInput1, text: e.target.value})
        input1.current.value = input1.current.value.replace(/\s/g, "");
    }
    useEffect(() => {
        setDivInput1( {...divInput1, width: inputManager1.current.getBoundingClientRect().width})
        if (Number(inputManager1.current.getBoundingClientRect().width) >= 290 &&
            Number(inputManager1.current.getBoundingClientRect().width) <= 400) {
            input1.current.style.width = inputManager1.current.getBoundingClientRect().width + 'px'
            input1.current.style.height = '30px'
        } else if (Number(inputManager1.current.getBoundingClientRect().width) <= 290 &&
            Number(inputManager1.current.getBoundingClientRect().width) <= 400) {
            input1.current.style.width = '290px'
            input1.current.style.height = '30px'
        } else if (Number(inputManager1.current.getBoundingClientRect().width) >= 400 &&
            Number(inputManager1.current.getBoundingClientRect().width) <= 770) {
            input1.current.style.height = '60px'
        } else if (Number(inputManager1.current.getBoundingClientRect().width) >= 770) {
            console.log('f')
            input1.current.value = input1.current.value.substr(0, 50)
        }
        console.log(inputManager1.current.getBoundingClientRect().width)


    }, [divInput1.text])



    const css = {
        body : {
            width: '400px',
            height: register ? '550px' : '400px',

            backgroundColor: '#0d1a2c',
            color: 'white',
            fontSize: '20px',
            fontFamily: 'Arial',
            borderRadius: '5px',

            margin: 'auto',
            marginTop: '50px',
            marginBottom: '100px',

            paddingLeft: '50px',
            paddingRight: '50px',
            paddingBottom: '30px',

            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
        },
        h1 : {
            marginTop: '30px',
        },
        text : {
            marginTop: '40px',
        },
        input : {
            width: '290px',
            height: '25px',

            backgroundColor: '#14325b',
            color: 'white',
            fontSize: '20px',
            fontFamily: 'Arial',
            outline: 'none',
            borderRadius: '5px',
            resize: 'none',

            marginTop: '5px',

            padding: '0',
            paddingTop: '5px',
            paddingLeft: '5px',
            paddingRight: '5px',
            border: '0',



        },
        inputClick : {
            width: '290px',
            height: '25px',

            backgroundColor: '#122641',
            color: 'white',
            fontSize: '20px',
            fontFamily: 'Arial',
            outline: 'none',
            resize: 'none',

            overflow: 'hidden',
            whiteSpace: 'normal',
            borderRadius: '5px',

            marginTop: '5px',

            padding: '0',
            paddingTop: '5px',
            paddingLeft: '5px',
            paddingRight: '5px',
            border: '0',
            boxShadow: '0 0 0 2px #fff',


        },
        inputManager : {
            minWidth: '295px',
            maxWidth: '400px',

            fontSize: '20px',
            visibility: 'hidden',
            whiteSpace: 'no-wrap',
        },
        inputManagerManager : {
            width: '400px',
            height: '0',

            display: 'inline',
            visibility: 'hidden',
        },
        divButton : {
            marginTop: '40px',

            display: 'flex',
            alignItems: 'center',
        },
        button : {
            width: '120px',
            height: '40px',

            backgroundColor: '#14325b',
            fontSize: '24px',
            color: 'white',
            borderRadius: '5px',

            border: '0',
        },
        buttonActive : {
            width: '120px',
            height: '40px',

            backgroundColor: '#122a4b',
            color: 'white',
            fontSize: '24px',
            borderRadius: '5px',

            border: '0',
        },
        image : {
            maxWidth: '25px',

            marginLeft: '10px'
        },
    }

    const [cssInput1, setCssInput1] = useState(css.input)
    const [cssInput2, setCssInput2] = useState(css.input)
    const [cssButton, setCssButton] = useState(css.button)

    return (
        <div style={css.body}>
            <h1 style={css.h1}>Sign In</h1>


            <text style={css.text}>E-mail:</text>

            <textarea style={cssInput1}
                   ref={input1}
                   onFocus={() => focusOnInput(1)}
                   onBlur={() => unFocusOnInput(1)}
                   onChange={(e) => changeWidthInput(e,1)}
            />

            <div style={css.inputManagerManager}>
                <text ref={inputManager1} style={css.inputManager}
                >{divInput1.text}</text>
            </div>




            <text style={css.text}>Password:</text>

            <input type="text" style={cssInput2}
                   onFocus={() => focusOnInput(2)}
                   onBlur={() => unFocusOnInput(2)}
                   onChange={(e) => changeWidthInput(e,2)}
            />
            <div style={css.inputManagerManager}>
                <text style={css.inputManager}>{divInput2.text}</text>
            </div>
            <div style={css.divButton}>
                <button style={cssButton}
                        onMouseUp={() => {setCssButton(css.button)}}
                        onMouseDown={() => {setCssButton(css.buttonActive)

                            setButtonCon(true)}}
                >Confirm</button>
                {confirmButton()}
            </div>
        </div>
    )
}
export default SingIn
