import React from 'react';





/*
    PROPS


    1 type
      - text
      - photoRight
      - photoLeft

    2 text
      - your text

    3 image
      - your image

    4 cloud
      - true
      - false
 */
function BlogDiv(props) {

    const css = {
        body : {
            width: props.cloud ? '70%' : '100%',
            height: 'auto',
            paddingRight: props.cloud ? '15%' : '0',
            paddingLeft: props.cloud ? '15%' : '0',
            marginTop: props.cloud ? '50px' : '0',
            marginBottom: props.cloud ? '50px' : '0',

            paddingTop: '30px',
            paddingBottom: '30px',


            backgroundColor: '#0d1a2c',

            fontSize: '16px',
        },
        text : {
            width: props.cloud ? '60%' : '80%',
            wordWrap: 'break-word',
        }



    }







    if (props.type === 'text') {
        return (
            <div style={css.body}>
                <text style={css.text}>{props.text}</text>



            </div>
        )
    } else if (props.type === 'photoRight') {
        return (
            <div style={css.body}>




            </div>
        )
    } else if (props.type === 'photoLeft') {
        return (
            <div style={css.body}>




            </div>
        )
    } else {
        console.log('ERROR')
    }

}

export default BlogDiv
