import image from "../image"
import {useState, useRef, useEffect} from 'react'

function SingIn(props) {
    const [register, setRegister] = useState(props.register)
    const focusOnInput = (number) => {
        if (number === 1) {
            setCssInput1(css.inputClick)
        } else if (number === 2) {
            setCssInput2(css.inputClick)
        }
    }
    const unFocusOnInput = (number) => {
        if (number === 1) {
            setCssInput1(css.input)
        } else if (number === 2) {
            setCssInput2(css.input)
        }
    }
    const confirmButton = () => {

        if (buttonCon && buttonConMeneger.current) {
            buttonConMeneger.current = false
            setTimeout(() => {
                buttonConMeneger.current = true
                setButtonCon(false)
            },1500)
            return <img src={image.loading} style={css.image}/>
        } else if (buttonCon) {
            return <img src={image.loading} style={css.image}/>
        }
    }
    const changeWidthInput = (e, number) => {
        setDivInput1({...divInput1, text: e.target.value})



    }

    const css = {
        body : {
            width: '400px',
            height: register ? '550px' : '400px',
            backgroundColor: '#0d1a2c',
            margin: 'auto',
            marginTop: '50px',
            marginBottom: '100px',
            paddingLeft: '50px',
            paddingRight: '50px',
            paddingBottom: '30px',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
            color: 'white',
            fontSize: '20px',
            fontFamily: 'Arial',
            borderRadius: '5px'
        },
        h1 : {
            marginTop: '30px',
        },
        text : {
            marginTop: '40px',
        },
        input : {
            width: '295px',
            border: '0',
            padding: '0',
            paddingLeft: '5px',
            height: '30px',
            marginTop: '5px',
            fontSize: '20px',
            borderRadius: '5px',
            outline: 'none',
            backgroundColor: '#14325b',
            color: 'white',
        },
        inputClick : {
            width: '295px',
            border: '0',
            boxShadow: '0 0 0 2px #fff',
            padding: '0',
            paddingLeft: '5px',
            height: '30px',
            marginTop: '5px',
            fontSize: '20px',
            borderRadius: '5px',
            outline: 'none',
            backgroundColor: '#122641',
            color: 'white',
            overflow: 'hidden',
            whiteSpace: 'normal',
        },
        inputManager : {
            minWidth: '295px',
            maxWidth: '400px',
            visibility: 'hidden',
            whiteSpace: 'no-wrap',
            fontSize: '20px',
        },
        inputManagerManager : {
            visibility: 'hidden',
            display: 'inline',
            width: '400px',
            height: '0',
        },
        divButton : {
            display: 'flex',
            alignItems: 'center',
            marginTop: '40px',
        },
        button : {
            width: '120px',
            height: '40px',
            fontSize: '24px',
            backgroundColor: '#14325b',
            border: '0',
            color: 'white',
            borderRadius: '5px',
        },
        buttonActive : {
            width: '120px',
            height: '40px',
            fontSize: '24px',
            backgroundColor: '#122a4b',
            border: '0',
            color: 'white',
            borderRadius: '5px',
        },
        image : {
            maxWidth: '25px',
            marginLeft: '10px'
        },





    }
    const [cssInput1, setCssInput1] = useState(css.input)
    const [cssInput2, setCssInput2] = useState(css.input)
    const [divInput1, setDivInput1] = useState({
        text: '',
        width: 0
    })
    const [divInput2, setDivInput2] = useState({
        text: '',
        width: 0
    })
    const [cssButton, setCssButton] = useState(css.button)
    const [buttonCon, setButtonCon] = useState(false)
    const buttonConMeneger = useRef(true)
    const [inputWidth1, setInputWidth1] = useState(280)
    const inputManager1 = useRef()

    useEffect(() => {


        if (inputManager1.current.getBoundingClientRect().width >= 280) {
            setInputWidth1(inputManager1.current.getBoundingClientRect().width)
        }

        setDivInput1( {...divInput1, width: inputManager1.current.getBoundingClientRect().width})
    }, [divInput1.text])


    return (
        <div style={css.body}>
            <h1 style={css.h1}>Sign In</h1>
            <text style={css.text}>E-mail:</text>
            <input type="text" style={cssInput1}
                   ref={inputWidth1}
                   onFocus={() => focusOnInput(1)}
                   onBlur={() => unFocusOnInput(1)}
                   onChange={(e) => changeWidthInput(e,1)}
            />
            <div style={css.inputManagerManager}>
                <text ref={inputManager1} style={css.inputManager}
                >{divInput1.text}</text>
            </div>
            <text style={css.text}>Password:</text>
            <input type="text" style={cssInput2}
                   onFocus={() => focusOnInput(2)}
                   onBlur={() => unFocusOnInput(2)}
                   onChange={(e) => changeWidthInput(e,2)}
            />
            <div style={css.inputManagerManager}>
                <text style={css.inputManager}>{divInput2.text}</text>
            </div>
            <div style={css.divButton}>
                <button style={cssButton}
                        onMouseUp={() => {setCssButton(css.button)}}
                        onMouseDown={() => {setCssButton(css.buttonActive)

                            setButtonCon(true)}}
                >Confirm</button>
                {confirmButton()}
            </div>
        </div>
    )
}
export default SingIn
